pan&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            p[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;][j] = (preU[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;][&lt;span class=&#34;built_in&#34;&gt;min&lt;/span&gt;(j + d, m)] - preU[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;][&lt;span class=&#34;built_in&#34;&gt;max&lt;/span&gt;(j - d - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, (&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;)&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)] + MOD) % MOD;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        preP[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;][j] = (preP[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;][j - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] + p[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;][j] + MOD) % MOD;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; (&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; i = &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;; i &amp;lt;= n; i++) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; (&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; j = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;; j &amp;lt;= m; j++) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (s[i][j] == &lt;span class=&#34;string&#34;&gt;&amp;#x27;X&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                u[i][j] = (preP[i - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;][&lt;span class=&#34;built_in&#34;&gt;min&lt;/span&gt;(j + d - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, m)] - preP[i - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;][&lt;span class=&#34;built_in&#34;&gt;max&lt;/span&gt;(j - d, (&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;)&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)] + MOD) % MOD;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            preU[i][j] = (preU[i][j - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] + u[i][j]) % MOD;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; (&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; j = &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;; j &amp;lt;= m; j++) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (s[i][j] == &lt;span class=&#34;string&#34;&gt;&amp;#x27;X&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                p[i][j] = (preU[i][&lt;span class=&#34;built_in&#34;&gt;min&lt;/span&gt;(j + d, m)] - preU[i][&lt;span class=&#34;built_in&#34;&gt;max&lt;/span&gt;(j - d - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, (&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;)&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)] + MOD) % MOD;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            preP[i][j] = (preP[i][j - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;] + p[i][j]) % MOD;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cout &amp;lt;&amp;lt; preP[n][m] &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;

 &lt;blockquote&gt;
&lt;p&gt;&lt;strong&gt;指导老师&lt;/strong&gt;：毛明松&lt;br /&gt;
&lt;strong&gt;编撰&lt;/strong&gt;：衷铭川（大数据 231 班，程设协会负责人）&lt;br /&gt;
&lt;strong&gt;友链&lt;/strong&gt;：&lt;a href=&#34;https://duoxichangan.github.io/2025/03/26/%E7%A8%8B%E5%BA%8F%E8%AE%BE%E8%AE%A1/2025%E4%BF%A1%E6%81%AF%E5%AD%A6%E9%99%A2%E8%93%9D%E6%A1%A5%E6%9D%AF%E4%B8%93%E9%A2%98%E8%AE%AD%E7%BB%83/%E4%B8%93%E9%A2%98%E5%9B%9B%E3%80%90%E5%9F%BA%E7%A1%80%E7%AE%97%E6%B3%95%E3%80%91/&#34;&gt;其实连按部就班也比想象中难&lt;/a&gt;&lt;br /&gt;
