[i], a[j] = a[j], a[i]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    quick_sort(a, l, j)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    quick_sort(a, j + &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, r)&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
